import Header from '../';
import Footer from '../../components/Footer';

export default function Layout() {
  return (
    <>
      <Header />
      <main className="pt-16 pb-28 min-h-screen bg-gray-100">
        <Outlet />
      </main>      
      <Footer />
    </>
  );
}
